import React, {useState} from "react"
import Counter from "./counter";

const CountersList = () => {
    const initialState = [
        {id: 0, value: 0, name: 'ненужная вещь'},
        {id: 1, value: 4, name: 'ложка'},
        {id: 2, value: 0, name: 'вилка'},
        {id: 3, value: 0, name: 'не моя вещь'},
        {id: 4, value: 0, name: 'меганужная вещь'},
    ]

    const [counters, setCounters] = useState(initialState)

    const handleDelete = (id) => {
        const newCounters = counters.filter(c => c.id !== id)
        setCounters(newCounters)
    }

    const handleReset = () => {
        setCounters(initialState)
    }

    const handleIncrement = (id) => {
        setCounters(counters.map(c => c.id === id ? {...c, value: c.value + 1} : {...c}))
    }

    const handleDecrement = (id) => {
        setCounters(counters.map(c => c.id === id ? {...c, value: c.value - 1} : {...c}))
    }


    return (
        <>
            {counters.map(count => <Counter
                key={count.id}
                {...count}
                onDelete={handleDelete}
                onIncrement={handleIncrement}
                onDecrement={handleDecrement}
            />)}
            <button className='btn btn-primary btn-sm m-2' onClick={handleReset}>reset</button>
        </>
    )
}

export default CountersList
