import React, {useState} from "react";

const Counter = (props) => {
    const {value} = props;

    const formatValue = () => {
        return value === 0 ? "empty" : value;
    }
    const getBadgeClasses = () => {
        let classes = "badge m-2 ";
        classes += value === 0 ? "bg-warning" : "bg-primary";
        return classes;
    }
    return (
        <div>
            <span>{props.name}</span>
            <span className={getBadgeClasses()}>{formatValue()}</span>
            <button className='m-lg-3 btn btn-primary btn-sm m-2' onClick={() => props.onIncrement(props.id)}>+</button>
            <button className='m-2 btn btn-primary btn-sm m-2' onClick={() => props.onDecrement(props.id)}>-</button>
            <button className='m-2 btn btn-danger btn-sm m-2' onClick={() => props.onDelete(props.id)}>delete</button>
        </div>
    )
}

export default Counter;
