import React, {useState} from "react"

const Counter =() => {
    const [count, setCount] = useState(0);
    const [tags, setTags] = useState(["tag1", "tag2", "tag3"]);
    const formatCount =() => {
        return count === 0 ? "empty" : count;
    }
    const getBageClasses = () => {
        let classes = "badge m-2 ";
        classes += count === 0 ? "bg-warning" : "bg-primary";
        return classes;
    };

    const handleIncrement = () => {
        setCount((prevState)=> prevState+ 1);
    }
    const handleDicrement = () => {
        setCount((prevState)=> prevState - 1);
    }
    const handleTagChange =(id) => {
        setTags((prevState) => prevState.filter());
    }

    return (
        <>
            <ul>
                {tags.map((tag) => (
                    <li key={tag} className="btn btn-primary btn-sm m-2" onClick={handleTagChange}> {tag}</li>
                ))}

            </ul>
            <span className={getBageClasses()}>{formatCount()}</span>
            <button className="btn btn-primary m-2" onClick={handleIncrement}>
                +
            </button>
            <button className="btn btn-primary m-2" onClick={handleDicrement}>
                -
            </button>
        </>
    )
}

export default Counter;
